import React from "react";
import { View, Text, StyleSheet } from "react-native";

const App = () =>
{
    return (
        <View style={
            MyStyle.ViewStyle
        }>            
        <Text style={MyStyle.ViewStyle}>Hello World </Text>
        </View>
    );
}

const MyStyle = StyleSheet.create(
    {
        ViewStyle: {
            width:100,
            height:100,
            backgroundColor:'pink',
            alignItems: 'center',
            justifyContent: 'center',
        },
        TextStyle:
        {
            color:'black'
        }
    }
)

export default App;
